import telebotimport sqlite3TOKEN = "1375970362:AAE8ZdZHn46q-R3lfQnZHsCJ3_GmL5-hpeE"bot = telebot.TeleBot(TOKEN)name = {}# connection = sqlite3.connect("person.db")# cursor = connection.cursor()# cursor.execute("CREATE TABLE IF NOT EXISTS info (id int, name text)")# cursor.close()# connection.close()@bot.message_handler(commands=['start'])def start(message):    sent = bot.send_message(message.chat.id, 'Как тебя зовут?')    bot.register_next_step_handler(sent, hello)def hello(message):    global name    connection = sqlite3.connect("person.dp")    name[message.chat.id] = message.text    info = message.chat.id, message.text    cursor = connection.cursor()    cursor.execute("CREATE TABLE IF NOT EXISTS info (id int, name text)")    cursor.execute("INSERT INTO info VALUES (?, ?)", info)    connection.commit()    cursor.close()    connection.close()    print("Новый пользователь: {name}".format(name=message.text));    bot.send_message(message.chat.id, 'Привет, {name}. Рад тебя видеть.'.format(name=message.text))@bot.message_handler(commands=['help'])def help(message):    bot.send_message(message.chat.id, helps())def helps():    ans = '/start - регистрация\n'    ans += '/help - вывод всех команд\n'    ans += '/settings - редактирование профиля'    return ans@bot.message_handler(commands=['settings'])def settings(message):    if message.chat.id in name:        bot.send_message(message.chat.id, 'Ваше имя: {name}'.format(name=name[message.chat.id]))@bot.message_handler(regexp="Привет")def handler_message(message):    if message.chat.id in name:        bot.send_message(message.chat.id, 'Привет, {name}. Рад тебя видеть.'.format(name=name[message.chat.id]))    else:        bot.send_message(message.chat.id, 'Я вас не знаю, зарегестрируйтесь пожалуйста')@bot.message_handler(regexp='')def handler_message(message):    bot.send_message(message.chat.id, 'Я не понимаю вас')bot.polling()