import telebotTOKEN = "1375970362:AAE8ZdZHn46q-R3lfQnZHsCJ3_GmL5-hpeE"bot = telebot.TeleBot(TOKEN);name = "QWE"@bot.message_handler(commands=['start'])def start(message):    sent = bot.send_message(message.chat.id, 'Как тебя зовут?')    bot.register_next_step_handler(sent, hello)def hello(message):    global name    name = message.text    bot.send_message(message.chat.id,'Привет, {name}. Рад тебя видеть.'.format(name=message.text))@bot.message_handler(commands=['help'])def help(message):    bot.send_message(message.chat.id, helps())def helps():    ans = '/start - регистрация\n/help - вывод всех команд\n/settings - редактирование профиля';    return ans@bot.message_handler(commands=['settings'])def settings(message):    bot.send_message(message.chat.id, 'Ваше имя: {name}'.format(name=name))bot.polling()